#ifndef MDS_ENVIRONMENT_H
#define MDS_ENVIRONMENT_H


//=============================================================================
/*
 * Linux definitions.
 */

#ifdef _LINUX

// Include POSIX and XOPEN linux extensions
#ifndef _POSIX_C_SOURCE
#   define _POSIX_C_SOURCE      199506L
#endif // _POSIX_C_SOURCE

#ifndef _XOPEN_SOURCE
#   define _XOPEN_SOURCE        500
#endif // _XOPEN_SOURCE

#ifndef _REENTRANT
#   define _REENTRANT
#endif // _REENTRANT

#ifndef _MULTI_THREADED
#   define _MULTI_THREADED
#endif // _MULTI_THREADED

#endif // _LINUX


//=============================================================================
/*
 * Windows definitions.
 */

#ifdef _WIN32

// Disable security warnings generated by MS Visual C++ 2005 compiler
#if defined(_MSC_VER) && (_MSC_VER >= 1400)
#   pragma warning(disable:4996)
#endif // _MSC_VER

#endif // _WIN32


#endif // MDS_ENVIRONMENT_H
